// SPDX-License-Identifier: Unset

pragma solidity ^0.8.7;
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";

abstract contract SendableWithMessage is ERC721 {

    mapping(uint256 => string) public messages;

    event SendCardEvent(address to, string message, uint256 tokenId);

    function sendWithMessage(address to, string memory message, uint256 tokenId) public {
        require(_isApprovedOrOwner(_msgSender(), tokenId), "ERC721: not owner nor approved");
        safeTransferFrom(msg.sender, to, tokenId);

        emit SendCardEvent(to, message, tokenId);
    }

    function sendWithOnChainMessage(address to, string memory message, uint256 tokenId) public {
        require(_isApprovedOrOwner(_msgSender(), tokenId), "ERC721: not owner nor approved");
        safeTransferFrom(msg.sender, to, tokenId);
        messages[tokenId] = message;

        emit SendCardEvent(to, message, tokenId);
    }

    function setMessage(uint256 tokenId, string memory message) public {
        require(_isApprovedOrOwner(_msgSender(), tokenId), "ERC721: not owner nor approved");
        messages[tokenId] = message;
    }
}

